import React, { useEffect, useState } from "react";
import {useParams, Link} from 'react-router-dom';
import axios from "axios";


function ArticleShow() {
    const [article, setArticle] = useState([]);
    const [auth,setAuth] = useState(false);
    const [comments, setComments] = useState([]);
    const { id } = useParams();
    const csrf= document.querySelector('meta[name="csrf-token"]').getAttribute('content');
    axios.defaults.headers['X-CSRF-Token'] = csrf;
    useEffect(() => {
    axios.get(`http://localhost:3000/articles/${id}`,{headers: {'Accept': 'application/json',}
    })
    .then(response => {
      console.log(response.data);
      setArticle(response.data.article)
      setComments(response.data.comments)
      setAuth(response.data.res)
      console.log(article);
    })
    .catch(error => {
      console.error('Error fetching data:', error);
    });
    }
    ,[]
    )
    function deletehandler(){
        axios.delete(`http://localhost:3000/articles/${id}`
    )
    .then(response =>
        window.location.href="/articles"
    )
    .catch(error => {
      console.error('Error fetching data:', error);
    });}
    function deletehandler(){
        axios.delete(`http://localhost:3000/articles/${id}`
    )
    .then(response =>
        window.location.href="/articles"
    )
    .catch(error => {
      console.error('Error fetching data:', error);
    });}

    function deleteComments(comment_id){
        axios.delete(`http://localhost:3000/articles/${article.id}/blog_comments/${comment_id}`)
    }

    function editComments(comment_id){
        axios.put(`http://localhost:3000/articles/${article.id}/blog_comments/${comment_id}/edit`)
    }

    return (
        <>
        <h1>{article.title}</h1>
        <p>{article.content}</p>
        <ul>
  {comments.map(comment => (
    <li key={comment.id}>
      {comment.content}
      {!auth && <button onClick={() => editComments(comment.id)}>Edit</button>}
      {!auth && <button onClick={() => deleteComments(comment.id)}>Delete</button>}
    </li>
  ))}
</ul>

        {console.log(article.title)}
        { auth ? 
       ( <><Link to={`/articles/${article.id}/edit`}>Edit</Link>
       </>):null}
       { auth ? 
       ( <><button onClick={() => deletehandler()}>Delete</button>
       </>):null}
        <Link to={`/articles/`}>Back</Link>
        </>
    )
}

export default ArticleShow;
