import React from "react";

function Articles() {
  const rootDiv = document.getElementById("root");
  const articles= JSON.parse(rootDiv.getAttribute("articles_data"));
  const currentUser =window.currentUser || null;
  return (
    <div>
      <h1>Articles List</h1>
      <ul>
        {articles.map((article) => (
          <li key={article.id}><a href={`/articles/${article.id}`}>{article.title}</a></li>
        ))}
      </ul>
      <a href="/users">View users</a><br/>
      {currentUser ? ( <a href="/articles/new"></a>) : 
      (<p>Please log in to create articles <br/><a href="/users/sign_in">Log in</a></p> )}
      {!currentUser ? (<button onClick="/users/sign_up">Sign up</button>): null}
    </div>
  );
}

export default Articles
