import React from "react";
import { useState, useEffect } from "react";
import { useParams, Link} from 'react-router-dom';
import axios from "axios";
function ArticleEdit(){
    const [article, setArticle] = useState({title: "", content: ""});
    const [title, setTitle] = useState(article.title);
    const [content, setContent] = useState(article.content);
    const { id } = useParams(); 
    function titlehandler(event){
        setTitle(event.target.value);
    }

    function contenthandler(event){
        setContent(event.target.value);
    }
     useEffect(() => {
            axios.get(`http://localhost:3000/articles/${id}`,{headers: {'Accept': 'application/json',}
            })
            .then(response => {

              console.log(response.data);
              setArticle(response.data.article)
              setTitle(response.data.article.title)
              setContent(response.data.article.content)
              console.log(article);
            })
            .catch(error => {
              console.error('Error fetching data:', error);
            });
            }
            ,[]
            )

    function updatehandler() {
        const csrfToken = document.querySelector('meta[name="csrf-token"]')?.content;
        if (!csrfToken) {
            console.error("CSRF token missing");
            return;
        }

        axios.put(`/articles/${article.id}`, 
            {article: { id, title, content}},
            {
                headers: {
                "X-CSRF-Token": csrfToken,
                "Content-Type": "application/json",
            },

        })
        .then(response => {
            alert("Article created successfully")
            window.location.href=`/articles/${id}`
        })
        .catch(error => {alert("Article updated successfully")
            if(error.ok){
                window.location.href=`/articles/${id}`
            }
        });
    }

    return (
        <>
        <h2>Title</h2>
        <input type="text" onChange={titlehandler} value={title}/>
        <h2>Content</h2>
        <textarea 
  rows="10" 
  cols="100" 
  onChange={contenthandler} 
  value={content}
/>
        <button onClick={() => updatehandler()} type="submit">Submit</button>
        <Link to={`/articles/${id}`}>Cancel</Link>
        <Link to="/articles">Home</Link>
                </>
    )
}

export default ArticleEdit
