import React from "react";
import { useEffect, useState } from "react";
function Articles() {
 // const rootDiv = document.getElementById("root");
 // const articles= JSON.parse(rootDiv.getAttribute("articles_data"));
 const rootDiv = document.getElementById("root");
  const [articles,setArticles] = useState([]);
 // const articles= JSON.parse(rootDiv.getAttribute("articles_data"));
  useEffect(() => {
    fetch("http:localhost::3000/articles", { headers: { Accept: "application/json" } })

    .then((res) => {
        console.log(res);
        if(res){
            alert("Success");
        }
        return res.json();
    })
    .then((data) => setArticles(data))
    .then(<ul>
        {articles.map((article) => (
          <li key={article.id}><a href={`/articles/${article.id}`}>{article.title}</a></li>
        ))}
      </ul>)
    .catch((err) => alert("There is some problem.\nPlease check it."))
  }, [])
  return (
    <div>
      <h1>Articles List</h1>
      <a href="/users">View users</a><br/>
      <a href="/articles/new">Create article</a>
    </div>
  );
}

export default Articles
