import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
function UserShow() {
    const [user, setUser] = useState();
    const [auth, setAuth] = useState(true);

    const [articles, setArticle] = useState([]);
    const { id } = useParams();
    const csrf = document.querySelector('meta[name="csrf-token"]').getAttribute('content');
    axios.defaults.headers['X-CSRF-Token'] = csrf;
    const [fake, setFake] = useState();
    useEffect(() => {
        axios.get(`http://localhost:3000/users/${id}`, {
            headers: { 'Accept': 'application/json', }
        })
            .then(response => {
                if (response.data.res == false) {
                    setUser(response.data.user)
                    setArticle(response.data.articles)
                    setFake(response.data.fake)
                    setAuth(false)
                }
                else {
                    window.location.href = "/not_found"
                }
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }
        , []
    )

    function deletehandler() {
        axios.delete(`http://localhost:3000/users/${id}`
        )

            .then(response =>
                window.location.href = `http://localhost:3000/users/`
            )

            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    return (
        <>
            <ul>
                {articles.map((article) => (
                    <li key={article.id}>
                        <Link to={`/articles/${article.id}`}>{article.title}</Link>
                    </li>
                ))}
            </ul>

            {!fake && <button onClick={() =>deletehandler} style={{ backgroundColor: "red", color: "white", marginTop: "10px" }}>
                Delete Account
            </button>}
            {!auth && (
                      <Link to="/articles/new" className="btn btn-success">
                        Create article
                      </Link>
                    )}
        </>
    );
}

export default UserShow;
