import React, { useEffect, useState } from "react";
import {useParams } from 'react-router-dom';
import axios from "axios";


function ArticleShow() {
    const [article, setArticle] = useState([]);
    const [auth,setAuth] = useState(false);
    const { id } = useParams();
    useEffect(() => {
    axios.get(`http://localhost:3000/articles/${id}`,{headers: {'Accept': 'application/json',}
    })
    .then(response => {
      //response.to_json();
      console.log(response.data);
      setArticle(response.data.article)
      setAuth(response.data.res)
      console.log(article);
    })
    .catch(error => {
      console.error('Error fetching data:', error);
    });
    }
    ,[]
    )
    function deletehandler(){axios.delete(`http://localhost:3000/articles/${id}`,{headers: {'Accept': 'application/json',}
    })
    .catch(error => {
      console.error('Error fetching data:', error);
    });}

    return (
        <>
        <h1>{article.title}</h1>
        <p>{article.content}</p>
        {console.log(article.title)}
        { auth ? 
       ( <><a href={`/articles/${article.id}/edit`}>Edit</a>
       <button onClick={deletehandler}>Delete</button></>):null}
        <a href={`/articles/`}>Back</a>


        </>
    )
}

export default ArticleShow;
