import React from "react";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
function ArticleEdit(){
    const rootDiv = document.getElementById("root");
    const article = JSON.parse(rootDiv.getAttribute("article"));
    const user = JSON.parse(rootDiv.getAttribute("user"));
    const [title, setTitle] = useState("Hello moto");
    const [content, setContent] = useState("Hello world");
    const history = useHistory();
    const id= article.id;
    function titlehandler(event){
        setTitle(event.target.value);
    }

    function contenthandler(event){
        setContent(event.target.value);
    }
    function updatehandler() {
        const csrfToken = document.querySelector('meta[name="csrf-token"]').getAttribute("content");

        axios.put(`/articles/${article.id}`, {
            article: {title: title,  
            content: content}
        }, {
            headers: {
                "Content-Type": "application/json",
                "X-CSRF-Token": csrfToken,  // CSRF Token if needed
            }
        })
        .then(response => {
            console.log("Article updated successfully:", response.data);

        })
        .catch(error => {
            console.error("Error updating article:", error.response?.data || error.message);
            history.push(`articles`)
        });
    } 
    return (
        <>
        <h2>Title</h2>
        <input type="text" onChange={titlehandler}/>
        <h2>Content</h2>
        <input type="textarea" onChange={contenthandler}/>
        <button onClick={updatehandler}>Submit</button>
        </>
    )
    }




export default ArticleEdit
